	public static function getMensagem($code = '')
	{
		$table = '<style>.table-text-center th, .table-text-center td{ text-align:center; }</style><table width="100%" cellspacing="0" cellpadding="10" border="0" class="table table-text-center"><thead><tr bgcolor="#f4f4f4"><th aling="right">Código Resposta</th><th>Definição</th><th>Significado</th><th>Ação</th><th style="text-align: center">Permite Retentativa</th></tr></thead><tbody>';
		$table .= self::$array[ $code ];
		$table .= '</body></table>';
		return $table;
	}
